memberdef.initializer:
template <typename T1, typename T2> auto operator op##=(T1 &a, const T2 &b) {              \
	return a = a op b;                                                                 \
}

struct {                                                                                   \
	template <typename... Ts> decltype(auto) operator()(Ts &&... args) const {         \
		return (func)(forward<decltype(args)>(args)...);                           \
	}                                                                                  \
}
